namespace JSONApi;


class Relationship extends json_ready
{
    protected $data;
    /**
     * @var bool flag used to indicate that relation is of type 1:1
     */
    private $type_one2one;
    protected $links;
    /**
     * @var bool flag used to indicate that for the specified relationship no data is available (valid for 1:n relations)
     */
    private $nodata=true;
    /**
     * @var int total number of relations when relation type is 1:n
     */
    private $total;


    /**
     * @param $data
     * @param null $links
     * @return mixed
     * @throws \Exception
     */
    static function factory($data,$links=null)
    {
        if(!is_null($links) && !($links instanceof  Links))
            throw new \Exception("Invalid Links object");

        switch($data->type) {
            case "array":
                return self::factory_one2many(isset($data->data)?$data->data:null,isset($data->data)?$data->total:null,$links);
            case "object":
                return self::factory_one2one(isset($data->data)?
                    $data->data:null,
                    $links);
        }
    }



    /**
     * @param $data
     * @param $links
     * @return Relationship
     * @throws \Exception
     */
    static private function factory_one2many($data,$total,$links)
    {
        $rs = new self(false);
        if(!is_null($links))
            $rs->setLinks($links);
        if($data) {
            $rs->setTotal($total);
            foreach ($data as $relData) {
                $rs->addRelation(
                    ResourceIdentifier::factory($relData)
                );
            }
        }

        return $rs;
    }

    /**
     * @param $data
     * @param Links $links
     * @return mixed
     * @throws \Exception
     */
    static private function factory_one2one($data,$links)
    {

        $rs = new self(true);
        if(!is_null($links))
            $rs->setLinks($links);

        if(is_null($data)) {
            $rs->addRelation(null);
            return $rs;
        }

        $rs->addRelation(
            ResourceIdentifier::factory($data)
        );
        return $rs;
    }

    /**
     * Relationships constructor.
     * @param $type_one2one
     */
    private function __construct ($type_one2one)
    {
        $this->type_one2one = $type_one2one;
        if(!$type_one2one)
            $this->data = [];
    }

    /**
     * @param $links
     * @return $this
     */
    function &setLinks($links)
    {
        $this->links = $links;
        return $this;
    }

    /**
     * @param ResourceIdentifier $rel
     */
    function addRelation($rel)
    {
        $this->nodata = false;
        if($this->type_one2one)
            $this->data  = $rel;
        else
            $this->data[] = $rel;
    }



    function getRelation($name)
    {
        if(isset($this->rels[$name]))
            return $this->rels[$name];
        throw new \Exception("Invalid relation $name");
    }

    function json_data ()
    {

        if($this->type_one2one && is_null($this->data))
            return [
                "data"=>null,
                "links"=>$this->links
            ];
        if(!$this->type_one2one && $this->nodata)
            return [
                "links"=>$this->links
            ];
        if(!$this->type_one2one)
            $this->meta = (object)[
                "total"=>$this->total
            ];
        return parent::json_data(); // TODO: Change the autogenerated stub
    }

    private function setTotal ($total)
    {
        $this->total = $total;
    }


}